import React from 'react';
import ReactDOM from 'react-dom';

import './ItemViewer.css'
import {ItemBox} from './ItemBox'

export class ItemViewer extends React.Component {
	
	
	
	constructor(props){
		super(props);
		this.state = {
            DataisLoaded: false,
			count: 0,
			n_pages: 0,
			pages: []
			
        };
	}
	
	componentDidMount() {
		
		let api_url = "http://localhost:8000/api/items/"
		
		api_url = api_url.concat(this.props.search)
		
        fetch(api_url)
		
		.then((res) => res.json())
		.then((json) => {
			this.setState({
				items: json.results,
				DataisLoaded: true,
				count: json.count
				
				}
			);
			}
		);
		
		
		

	}
	

	
	render(){
		
		const { DataisLoaded, items } = this.state;
		if (!DataisLoaded) 
			return (
				<div class=''>
					<h1> Loading... </h1> 
				</div> 
			);
			
		let n_pages = Math.ceil(this.state.count / 4);
		
		const pages = [];
		
		for(let i = 0; i < n_pages; i++){
			pages.push(<p> kakka </p>);
		}
				
		return (	
			<div>
				<div class="wrapper">
				{
					items.map((item) => (
						
						<ItemBox title={item.title} price={item.price} desc={item.desc} />
						
					))
				}
				</div>
				
				<div>
					yoyoyoy
					<ul>{pages}</ul>
				</div>
				
				
			
			</div>
		);
	}
}
