import React from 'react';
import ReactDOM from 'react-dom';

import './ItemViewer.css'
import {ItemBox} from './ItemBox'

export class ItemViewer extends React.Component {
	
	constructor(props){
		super(props);
		this.state = {
            items: [],
            DataisLoaded: false,
			count: 0
        };
	}
	
	componentDidMount() {
		let api_url = "http://localhost:8000/api/items/"
		
		api_url = api_url.concat(this.props.search)
		
        fetch(api_url)
		
		.then((res) => res.json())
		.then((json) => {
			this.setState({
				items: json.results,
				DataisLoaded: true,
				count: json.count
				}
			);
			}
		)
		
		
	}
	
	static getDerivedStateFromProps(props, state) {
		return {title: props.favcol };
	}
	
	render(){
		
		const { DataisLoaded, items } = this.state;
		if (!DataisLoaded) 
			return (
				<div class=''>
					<h1> Loading... </h1> 
				</div> 
			);
				
		return (	
			<div class="wrapper">
			{
				items.map((item) => (
					
					<ItemBox title={item.title} price={item.price} desc={item.desc} />
					
				))
			}
			</div>
		);
	}
}
