import React from 'react';
import {BrowserRouter,
		Routes,
		Route
		} from "react-router-dom";

import './ItemViewer.css'
import {ItemViewerPage} from './ItemViewerPage'
import {ItemBox} from './ItemBox'




export class ItemViewer extends React.Component {
	
	
	
	constructor(props){
		super(props);
		this.state = {
            DataisLoaded: false,
			count: 0,
			n_pages: 0,
			pages: []
			
        };
	}
	
	componentDidMount() {

        fetch(this.props.search)
		
		
		
		.then((res) => res.json())
		.then((json) => {
			this.setState({
				DataisLoaded: true,
				count: json.count
			});
		});
		
		
		
	}
	

	
	render(){
		
		const { DataisLoaded, items } = this.state;
		if (!DataisLoaded) 
			return (
				<div class=''>
					<h1> Loading... </h1> 
				</div> 
			);
			
		let n_pages = Math.ceil(this.state.count / 4);
		
		const pages = [];
		
		for(let i = 1; i <= n_pages; i++){
			let path_str = "/";
			
			if (i > 1){
				path_str = path_str + "page" + i;
			}
			
			let search_arg = this.props.search + "page=" + i;
			
			pages.push(<p> kakka </p>);
			//pages.push(<Route path={path_str}> <ItemViewerPage search={search_arg} /> </Route>);
		}
				
		return (	
			<div>
				<BrowserRouter>
					<Routes>
					
					<Route path="/" component={<ItemViewerPage search="http://localhost:8000/api/items/?sold=false"/>} />
					
					</Routes>
				</BrowserRouter>
				
			</div>
		);
	}
}
