import React from 'react';
import {useState} from 'react' 
import './App.css';
import {ItemViewer} from './ItemViewer'
import {Home} from './Home'
import {Search} from './Search'
import {Account} from './Account'
import {TopBar} from './TopBar'
import Cookies from 'js-cookie'


import {BrowserRouter,
		Routes,
		Route,
		Link
		} from "react-router-dom";

export class App extends React.Component {

	constructor(props){
		super(props);

		this.state = {cart:[],
						n_items:0};



		this.count = 0;

		document.cookie = "test = hey"

	}


	getCookie(cookieName) {
	  let cookie = {};
	  document.cookie.split(';').forEach(function(el) {
		let [key,value] = el.split('=');
		cookie[key.trim()] = value;
	  })
	  return cookie[cookieName];
	}


	addToCart(id) {
		console.log("grjgrjgjdrigjrdoi")
		if (this.state.cart.indexOf(id) == -1 ) { 
			this.state.cart.push(id)
			this.count += 1;
			this.forceUpdate()
		}

	}






	render(){

		const api_url = "http://localhost:8080/api";
		const api_url_items = api_url + "/items";

		this.state.n_items = 1;

		const addToCart = (id) => {

			if (this.state.cart.indexOf(id) == -1 ) { 
				this.state.cart.push(id)
				this.count += 1;
				this.forceUpdate()
			}

		}
		const removeFromCart = (id) =>{
			this.state.cart.remove(id)
		}


		return (


			<BrowserRouter>
				<div>

					<TopBar itemCount={this.count}/>



					<div class='main-cont'>
						<Routes>
							<Route path="/shop/*" element={<Home search={api_url_items} addToCart={this.addToCart}/>} />
							<Route path="/search/*" element={<Search search={api_url_items} addToCart={this.addToCart}/>} />
						</Routes>

					</div>


				</div>

				<div>
					{this.getCookie("test")}
				</div>

			</BrowserRouter>
		);
	}
}
