import React from 'react';
import ReactDOM from 'react-dom';
import cat from './cat.jpg'

import './ItemBox.css'

export class ItemBox extends React.Component {
	
	constructor(){
		super();
		this.state = {added:false}
	}
	
	addToCart(){
		this.porps.addToCart(this.props.id);
		this.state.added = true;
	}
	
	
	render(){
		let label;
		if (this.state.added){
			label = <div> added </div>
		}else{
			label = <div> not added </div>
		}
		
		return (
			<div class="item-box">
				<div class="itembox-title-text">
					{this.props.title}
				</div>
				
				<img src={cat} class="item-box-img" width = "300">
					
				</img>
				
				<div class="itembox-price-text">
					{this.props.price}
				</div>
				
				<div class="itembox-desc-text">
					{this.props.desc}
				</div>
				
				<div>
					<button onClick={this.props.addToCart(this.props.id)}> add to cart </button>
						{label}
				</div>
				
				
				
			</div>
		);
	}
}
