import React from 'react';


import './ItemViewer.css'
import {ItemBox} from './ItemBox'

export class CartItemViewerPage extends React.Component {
	
	constructor(props){
		super(props);
		this.state = {
            items: [],
            DataisLoaded: false,
        };
		
		this.getItem=this.getItem.bind(this);
	}
	
	componentDidMount() {

		
        
	}
	
	
	getItem(id){
		
		let item
		
		fetch(this.props.search + "?id=" + id)
		
		.then((res) => res.json())
		.then((json) => {
			item = json.results
			
			
			console.log(item)
			return item
			
			
			}
		)
		
		
	}
	
	

	
	render(){
		
		let items = []
		
		this.props.cart_nibble.map((id) => ( 
			
			this.getItem(id).map((item) => (
				items.push(item)
				
				
			))
		))
				
		return (	
			<div class="wrapper">
			{
				
				items.map((item) => (
					
					<ItemBox id={item.id} title={item.title} price={item.price} desc={item.desc} call={this.props.call} action={this.props.action}/>
					
				))
			}
			
			
			</div>
		);
	}
}
