import React from 'react';
import {useState} from 'react' 
import './App.css';
import {ItemViewer} from './ItemViewer'
import {Home} from './Home'
import {Search} from './Search'
import {Account} from './Account'
import {TopBar} from './TopBar'


import {BrowserRouter,
		Routes,
		Route,
		Link
		} from "react-router-dom";

export class App extends React.Component {

	constructor(props){
		super(props);

		this.state = {cart:[]};




	}










	render(){

		const api_url = "http://localhost:8080/api";
		const api_url_items = api_url + "/items";


		const addToCart = (id) => {
			this.state.cart.push(id)
		}
		const removeFromCart = (id) =>{
			this.state.cart.remove(id)
		}


		return (


			<BrowserRouter>
				<div>

					<TopBar />



					<div class='main-cont'>
						<Routes>
							<Route path="/shop/*" element={<Home search={api_url_items} addToCart={addToCart}/>} />
							<Route path="/search/*" element={<Search search={api_url_items} addToCart={addToCart}/>} />
						</Routes>

					</div>


				</div>


				<div>
				{this.state.cart.length}
				</div>

			</BrowserRouter>
		);
	}
}
