import React from 'react';
import ReactDOM from 'react-dom';
import cat from './cat.jpg'

import './ItemBox.css'

export class ItemBox extends React.Component {
	
	constructor(){
		super();
		this.state = {added:false}
		
	}
	
	addToCart(){
		this.porps.addToCart(this.props.id);
		this.state.added = false;
	}
	
	
	render(){
		return (
			<div class="item-box">
				<div class="itembox-title-text">
					{this.props.title}
				</div>
				
				<img src={cat} class="item-box-img" width = "300">
					
				</img>
				
				<div class="itembox-price-text">
					{this.props.price}
				</div>
				
				<div class="itembox-desc-text">
					{this.props.desc}
				</div>
				
				<div>
					<button onClick={this.addToCart}> add to cart </button>
						{this.added
						? <div> not added </div>
						: <div> added </div>
						}
				</div>
				
				
				
			</div>
		);
	}
}
